import * as actionTypes from './actions';

const initialState={
    ingredients:{
        salad:0,
        bacon:0,
        meat:0,
        cheese:0
    },
    totalPrice:4  
};

const INGREDIENT_PRICES={
    salad:0.5,
    cheese:0.6,
    meat:1.5,
    bacon:1
}

const reducer = (state=initialState,action) => {
    switch(action.type){
        case actionTypes.ADD_INGREDIENT:
            return{
                ...state,
                ingredients:{
                    ...state.ingredients,
                    // ingredientName is a payload we get to the action here
                    [action.ingredientName]:state.ingredients[action.ingredientName]+1
                }

            };
        case actionTypes.REMOVE_INGREDIENT:
            return{
                ...state,
                ingredients:{
                    ...state.ingredients,
                    // ingredientName is a payload we get to the action here
                    [action.ingredientName]:state.ingredients[action.ingredientName]-1
                }

            };   
        default : 
            return state;

    }

}

export default reducer;
