import React, { Component } from 'react';
import Button from '../../../components/UI/Button/Button';
import classes from './ContactData.module.css';
import Spinner from '../../../components/UI/Spinner/Spinner';
import axios from '../../../axios-orders'
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {

    state = {
        orderForm: {
            name: {
                elementType: 'input',
                elementConfig: {
                type: 'text',
                placeholder: 'Your Name'
                },
                value: '',
                validation:{
                    required:true
                },
                valid:false
            },
            street: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Street'
                },
                value: '',
                validation:{
                    required:true
                },
                valid:false
            },
            zipCode: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'ZIP Code'
                },
                value: '',
                validation:{
                    required:true
                },
                valid:false
            },
            country: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Country'
                },
                value: '',
                validation:{
                    required:true,
                    minLength:5,
                    maxLength:5
                },
                valid:false
            },
            email: {
                elementType: 'input',
                elementConfig: {
                    type: 'email',
                    placeholder: 'Your E-Mail'
                },
                value: '',
                validation:{
                    required:true
                },
                valid:false
            },
            deliveryMethod: {
                elementType: 'select',
                elementConfig: {
                    options: [
                        {value: 'fastest', displayValue: 'Fastest'},
                        {value: 'cheapest', displayValue: 'Cheapest'}
                    ]
                },
                value: ''
            }
        },
        loading: false
    }



    checkValidity(value, rules) {
        let isValid = true;

        if (rules.required) {
            isValid = value.trim() !== '' && isValid;
        }

        if (rules.minLength) {
            isValid = value.length >= rules.minLength && isValid
        }

        if (rules.maxLength) {
            isValid = value.length <= rules.maxLength && isValid
        }

        return isValid;
    }

    orderHandler = (event) => {
        //avoid reloading the page(does not make a new request.)
        event.preventDefault();
        // console.log(this.props.ingredients);

        const formData = {};
        for (let formElementIdentifier in this.state.orderForm) {
            formData[formElementIdentifier] = this.state.orderForm[formElementIdentifier].value;
        }
        // const order = {
        //     ingredients: this.props.ingredients,
        //     price: this.props.price,
        //     orderData: formData
        // }
        // axios.post( '/orders.json', order )
        //     .then( response => {
        //         this.setState( { loading: false } );
        //         this.props.history.push( '/' );
        //     } )
        //     .catch( error => {
        //         this.setState( { loading: false } );
        //     } );





        this.setState({ loading:true });
        const order ={
        ingredients:this.props.ingredients,
        //recalculate the price on server (in real App)
        price:this.props.price,
        orderData: formData


    }

    axios.post('/orders.json',order)
    .then(response => {
        this.setState({ loading:false});
        //will take me back to home page after clicking order.
        this.props.history.push('/');
        // console.log(response)
    })
    .catch(error =>{
        //  console.log(error));
         this.setState({ loading:false});
    });

    }

    //here i want to clone deeply.(bcoz order object has nested objects 
    // and when we mutate using setState it will change original data.)
    inputChangedHandler = (event, inputIdentifier) => {
        const updatedOrderForm = {
            ...this.state.orderForm
        };
        //Example-email
        const updatedFormElement = { 
            ...updatedOrderForm[inputIdentifier]
        };
        //Setting its value
        updatedFormElement.value = event.target.value;
        updatedFormElement.valid = this.checkValidity(updatedFormElement.value, updatedFormElement.validation);


        // updatedFormElement.touched = true;
        updatedOrderForm[inputIdentifier] = updatedFormElement;
        console.log(updatedFormElement);
        this.setState({orderForm: updatedOrderForm});
    }

    render(){

        //convert orderForm object into array so that i can loop through.
        // i am pushing object into an array..
        //id:key -> name,street..
        //config:this.state.orderForm[key] ->stores all the config details.(right side data)
        const formElementsArray = [];
        for (let key in this.state.orderForm) {
            formElementsArray.push({
                id: key,
                config: this.state.orderForm[key]
            });
        }

        let form = ( 
        <form onSubmit={this.orderHandler}>
            {/* <Input inputtype="input"  type="text" name="name" placeholder="Your name" /> */}

            {formElementsArray.map(formElement => (
                    <Input 
                        key={formElement.id}
                        elementType={formElement.config.elementType}
                        elementConfig={formElement.config.elementConfig}
                        value={formElement.config.value}
                        invalid={!  formElement.config.valid}
                        changed={(event) => this.inputChangedHandler(event, formElement.id)} 
                        />
                ))}

            <Button btnType="Success">Order</Button>
        </form>
);
        if(this.state.loading){
            form = <Spinner />
        }
        return(
            <div className={classes.ContactData}>
                <h4>Enter Your Contact Details : </h4>
                {form}
            </div>

        );
    }
}

export default ContactData;
