import React, { Component } from 'react';
import Button from '../../../components/UI/Button/Button';
import classes from './ContactData.module.css';
import Spinner from '../../../components/UI/Spinner/Spinner';
import axios from '../../../axios-orders'
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {

    state = {
        orderForm: {
            name: {
                elementType: 'input',
                elementConfig: {
                type: 'text',
                placeholder: 'Your Name'
                },
                value: ''
            },
            street: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Street'
                },
                value: ''
            },
            zipCode: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'ZIP Code'
                },
                value: ''
            },
            country: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Country'
                },
                value: ''
            },
            email: {
                elementType: 'input',
                elementConfig: {
                    type: 'email',
                    placeholder: 'Your E-Mail'
                },
                value: ''
            },
            deliveryMethod: {
                elementType: 'select',
                elementConfig: {
                    options: [
                        {value: 'fastest', displayValue: 'Fastest'},
                        {value: 'cheapest', displayValue: 'Cheapest'}
                    ]
                },
                value: ''
            }
        },
        loading: false
    }

    orderHandler = (event) => {
        //avoid reloading the page(does not make a new request.)
        event.preventDefault();
        // console.log(this.props.ingredients);

        this.setState({ loading:true });
        const order ={
        ingredients:this.props.ingredients,
        //recalculate the price on server (in real App)
        price:this.props.price,


    }

    axios.post('/orders.json',order)
    .then(response => {
        this.setState({ loading:false});
        //will take me back to home page after clicking order.
        this.props.history.push('/');
        // console.log(response)
    })
    .catch(error =>{
        //  console.log(error));
         this.setState({ loading:false});
    });

    }

    render(){
        let form = ( 
        <form>
            <Input inputtype="input"  type="text" name="name" placeholder="Your name" />
            <Input inputtype="input" type="email" name="email" placeholder="Your email" />
            <Input inputtype="input" type="text" name="street" placeholder="Street" />
            <Input inputtype="input" type="text" name="pincode" placeholder="Your Pincode" />
            <Button btnType="Success" clicked={this.orderHandler}>Order</Button>
        </form>
);
        if(this.state.loading){
            form = <Spinner />
        }
        return(
            <div className={classes.ContactData}>
                <h4>Enter Your Contact Details : </h4>
                {form}
            </div>

        );
    }
}

export default ContactData;
