import React, { Component } from 'react';
import Button from '../../../components/UI/Button/Button';
import classes from './ContactData.module.css';

class ContactData extends Component {

    state={
        name:'',
        email:'',
        address:{
            street:'',
            pincode:''
        },
        loading:false
    }

    orderHandler = (event) => {
        //avoid reloading the page(does not make a new request.)
        event.preventDefault();
        // console.log(this.props.ingredients);

        this.setState({ loading:true });
        const order ={
        ingredients:this.props.ingredients,
        //recalculate the price on server (in real App)
        price:this.props.totalPrice,

        customer:{
            name:'Gunjan',
            address:{
                street:'Sreet1',
                zipcode:'110009',
                country:'India'
            },
            email:'gunjan.dawar@tothenew.com'
        },
        deliveryMethod:'fastest'
    }

    axios.post('/orders.json',order)
    .then(response => {
        this.setState({ loading:true , purchasing:false});
        // console.log(response)
    })
    .catch(error =>{
        //  console.log(error));
         this.setState({ loading:true ,purchasing:false});
    });

    }

    render(){
        return(
            <div className={classes.ContactData}>
                <h4>Enter Your Contact Details : </h4>
                <form>
                    <input className={classes.Input} type="text" name="name" placeholder="Your name" />
                    <input className={classes.Input} type="email" name="email" placeholder="Your email" />
                    <input className={classes.Input} type="text" name="street" placeholder="Street" />
                    <input className={classes.Input} type="text" name="pincode" placeholder="Your Pincode" />
                    <Button btnType="Success" clicked={this.orderHandler}>Order</Button>

                </form>

            </div>

        );
    }
}

export default ContactData;
